create table eden.members(	id serial not null		constraint members_pkey			primary key,	deviceid varchar(100) not null,	primaryemail varchar(100) not null,	primaryphone bigint not null,	firstname varchar(25) not null,	lastname varchar(30) not null,	confirmed boolean default false not null,	activemember boolean default false not null,	updatedby json,	primarypaymentmethod json,	suppliedgender char(6),	predictedgender char(6),	birthdate json);create unique index members_id_uindex	on eden.members (id);create unique index members_device_uindex	on eden.members (deviceid);create unique index members_primaryemail_uindex	on eden.members (primaryemail);create unique index members_primaryphone_uindex	on eden.members (primaryphone);create index members_phonefirstlastname_index	on eden.members (primaryphone, lastname, firstname);comment on column eden.members.id is 'System ID - not for human use.';comment on column eden.members.deviceid is 'base64 encoded UUID generated by UUID Server. - not for human use.';comment on column eden.members.primaryemail is 'This is the email address that is used to login to eden.';comment on column eden.members.primaryphone is 'This is the phone number without formatting.';comment on column eden.members.confirmed is 'This can only be true or false. This is set to true when the confirmation email is received back from the member.';comment on column eden.members.activemember is 'This can only be true or false. This is set to true when the confirmed is set.';comment on column eden.members.updatedby is 'String containing the Update type, date/time and user id.';comment on column eden.members.primarypaymentmethod is 'String containing type, nickname, third party token for the preferred method and other details to be defined in the future.';comment on column eden.members.suppliedgender is 'The gender the member identifies themselves by to eden.';comment on column eden.members.predictedgender is 'The gender eden has determined via algorithms.';comment on column eden.members.birthdate is 'String containing the day, month and/or year. All values are optional.';