create table eden.membermessages(	lovkey varchar(50) not null,	localeidentifier varchar(10) not null,	membermessage text not null,	updatedby json,	constraint membermessages_lovkey_localeidentifier_pk		primary key (lovkey, localeidentifier));comment on table eden.membermessages is 'This table is a look up for member messages by locale.';comment on column eden.membermessages.lovkey is 'This is the value that is used to look up the message that will be sent to the member.';comment on column eden.membermessages.localeidentifier is 'This is the code used to identify the langauge for the locale making the request. https://gist.github.com/jasef/337431c43c3addb2cbd5eb215b376179';comment on column eden.membermessages.membermessage is 'This is the message that will be displayed to the member.';comment on column eden.membermessages.updatedby is 'String containing the Update type, date/time and user id.';