create table slm.customermessages(	lovkey varchar(30) not null,	localeidentifier varchar(10) default 'en_US'::character varying not null,	customermessage text not null,	updatedby json,	constraint customermessages_lovkey_localeidentifier_pk		primary key (lovkey, localeidentifier));comment on table slm.customermessages is 'This table is a look up for customer messages by locale.';comment on column slm.customermessages.lovkey is 'This is the value that is used to look up the message that will be sent to the customer.';comment on column slm.customermessages.localeidentifier is 'This is the code used to identify the langauge for the locale making the request. https://gist.github.com/jasef/337431c43c3addb2cbd5eb215b376179';comment on column slm.customermessages.customermessage is 'This is the message that will be displayed to the customer.';comment on column slm.customermessages.updatedby is 'String containing the Update type, date/time and user id.';